const repeat = (s, length) => Array.from({ length }, () => s).join('');

module.exports = depiction => {
  const padding = 6;
  const lines = `\n${depiction}\n\n    ceci n'est pas un cactus\n`.split('\n');
  const horizMargin = repeat(' ', padding);
  const width = Math.max(...lines.map(line => line.length));
  const horiz = `@${repeat('=', width + padding * 2)}@`;
  const vert = lines.map(line =>
    `|${horizMargin}${line + repeat(' ', width - line.length)}${horizMargin}|`
  ).join('\n');
  return `\n${horiz}\n${vert}\n${horiz}`
}
